            }
        }

        if (item.hasAttribute('role')){
            if (item.getAttribute('role') == "presentation" && (!item.hasAttribute('tabindex') || item.getAttribute('tabindex') < 0)) {
                item.setAttribute('data-tng-img-ignored', true);
