		if (/^.+(\s.+)*$/.test(headers)) {
			headers = headers.split(' ');
			if (headers.length > 1) {
				var result = true;
				for (var i = 0; i < headers.length; i++) {
					var th = document.getElementById(headers[i]);;
					result = th ? th.closest('table') == item.closest('table') : false;
					if (!result) {
						break;
					}
				}

                if(result) {
                    return true;
                } else {
                    item.setAttribute('data-tng-headerInTable', 'false');
